import React from 'react';
import axios from 'axios';
import { useKioskStore } from '../store/kioskStore';
import { useQuery } from '@tanstack/react-query';
import { kioskTheme } from '../lib/theme';
import { cn } from '../lib/utils';
import { Button } from './ui/button';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from './ui/card';
import { Spinner } from '../App';

// Mock API function for TanStack Query
const fetchOrderStatusApi = async (orderId) => {
  console.log(`[MOCK API] /api/status/${orderId} called`);
  // Simulate network delay
  await new Promise(resolve => setTimeout(resolve, 500));

  // This would be a real DB lookup. We'll simulate it.
  const { order } = useKioskStore.getState();
  if (order && order.id === orderId) {
    return { status: order.status };
  }
  throw new Error('Order not found in mock state');
};

// Mock Razorpay Webhook Call
// This simulates Razorpay's server hitting our backend
const simulateWebhook = (orderId, setOrderStatus) => {
  console.log(`[MOCK WEBHOOK] Simulating payment for ${orderId}. Will confirm in ${MOCK_PAYMENT_TIME}ms.`);
  setTimeout(() => {
    console.log(`[MOCK WEBHOOK] Payment for ${orderId} is PAID.`);
    // In a real app, this happens on the backend.
    // Here, we just update the client-side store to simulate the backend update.
    setOrderStatus('PAID');
  }, MOCK_PAYMENT_TIME);
};


export default function PaymentScreen() {
  const { order, setOrderStatus, reset, selectedPerfumeId } = useKioskStore((state) => ({
    order: state.order,
    setOrderStatus: state.setOrderStatus,
    reset: state.reset,
    selectedPerfumeId: state.selectedPerfumeId
  }));

  // Get theme styles
  const { card, title } = kioskTheme({ perfume: selectedPerfumeId });

  // Start the mock webhook simulation on mount
  React.useEffect(() => {
    if (order && order.status === 'CREATED') {
      simulateWebhook(order.id, setOrderStatus);
    }
  }, [order, setOrderStatus]);

  // Use TanStack Query to poll for the order status
  const { data, isLoading } = useQuery({
    queryKey: ['orderStatus', order.id],
    queryFn: () => fetchOrderStatusApi(order.id),
    // Poll every 2 seconds
    refetchInterval: 2000,
    // Stop polling if the order is not in 'CREATED' state
    enabled: !!order && order.status === 'CREATED',
    // When status changes to 'PAID', this query will refetch, get the new status,
    // and the store will be updated by the `simulateWebhook` function.
    // The App.jsx router will then automatically switch screens.
  });

