  // TanStack Query mutation for dispatching
  const { mutate: dispatchOrder, isPending: isDispensing } = useMutation({
    mutationFn: dispatchOrderApi,
    onSuccess: (data) => {
      // On success (dispense finished), move to complete screen
      console.log('[React] Dispense complete. Moving to final screen.');
